import React, { Component, useState} from "react";
import Flashcard from "./components/Flashcard"




class App extends Component{
  state = {
    data:[]
  }

  componentDidMount(){
    fetch('http://localhost:8080/flashcards')
    .then(response => response.json())
    .then(data => {
      this.setState({data})
    }
    );


  }


  DisplayArrayElements({ data }) {
    const [currentIndex, setCurrentIndex] = useState(0);

    const handleClick = () => {
      setCurrentIndex(currentIndex + 1);
    };

    return (
      <div>
        <p>{this.state.data[{currentIndex}]}</p>
        <button onClick={handleClick}>Next</button>
      </div>
    );
  }

  render() {
    return (
      <div>
        {this.state.data.map(flashcard => <Flashcard info={flashcard} />)}
        <div>
          <button
            type="button"
            onClick={() => {this.state.data("blue")}}
          >Pevious</button>
          <button
            type="button"
            onClick={() => this.state.data("blue")}
          >Next</button>

          <DisplayArrayElements index/>
        </div>
      </div>
    );
  }
}

export default App;
